export function buscarCuidadosPaliativos(query: string): CuidadosPaliativosEntry[] {
  const lowerQuery = query.toLowerCase();
  return cuidadosPaliativosEnciclopedia.filter(entry =>
    entry.nombreEs.toLowerCase().includes(lowerQuery) ||
    entry.nombreEn.toLowerCase().includes(lowerQuery) ||
    entry.categoriaEs.toLowerCase().includes(lowerQuery) ||
    entry.categoriaEn.toLowerCase().includes(lowerQuery) ||
    entry.descripcionEs.toLowerCase().includes(lowerQuery) ||
    entry.descripcionEn.toLowerCase().includes(lowerQuery)
  );
}

export function getCuidadosPaliativosById(id: string): CuidadosPaliativosEntry | undefined {
  return cuidadosPaliativosEnciclopedia.find(entry => entry.id === id);
}

export function getCuidadosPaliativosPorCategoria(categoria: string): CuidadosPaliativosEntry[] {
  const lowerCategoria = categoria.toLowerCase();
  return cuidadosPaliativosEnciclopedia.filter(
    entry => entry.categoriaEs.toLowerCase() === lowerCategoria ||
             entry.categoriaEn.toLowerCase() === lowerCategoria
  );
}
