// -----------------------------------------------------------------------------
// HELPER FUNCTIONS
// -----------------------------------------------------------------------------

export function buscarEmergencias(query: string): EmergenciaEntry[] {
  const q = query.toLowerCase();
  return emergenciasEnciclopedia.filter(e =>
    e.nombreEs.toLowerCase().includes(q) ||
    e.nombreEn.toLowerCase().includes(q) ||
    e.categoriaEs.toLowerCase().includes(q) ||
    e.categoriaEn.toLowerCase().includes(q) ||
    e.descripcionEs.toLowerCase().includes(q) ||
    e.descripcionEn.toLowerCase().includes(q)
  );
}

export function getEmergenciaById(id: string): EmergenciaEntry | undefined {
  return emergenciasEnciclopedia.find(e => e.id === id);
}

export function getEmergenciasPorNivel(nivel: 'critica' | 'alta' | 'moderada'): EmergenciaEntry[] {
  return emergenciasEnciclopedia.filter(e => e.nivelUrgencia === nivel);
}

export function getEmergenciasPorCategoria(cat: string): EmergenciaEntry[] {
  const c = cat.toLowerCase();
  return emergenciasEnciclopedia.filter(e =>
    e.categoriaEs.toLowerCase().includes(c) ||
    e.categoriaEn.toLowerCase().includes(c)
  );
}
