export function buscarGeneticaConsejeria(termino: string): GeneticaConsejeriaEntry[] {
  const t = termino.toLowerCase();
  return GENETICA_CONSEJERIA_ENTRIES.filter(e =>
    e.nombreEs.toLowerCase().includes(t) ||
    e.nombreEn.toLowerCase().includes(t) ||
    e.descripcionEs.toLowerCase().includes(t) ||
    e.descripcionEn.toLowerCase().includes(t)
  );
}

export function getGeneticaConsejeriaById(id: string): GeneticaConsejeriaEntry | undefined {
  return GENETICA_CONSEJERIA_ENTRIES.find(e => e.id === id);
}

export function getGeneticaConsejeriaPorCategoria(categoria: string): GeneticaConsejeriaEntry[] {
  const c = categoria.toLowerCase();
  return GENETICA_CONSEJERIA_ENTRIES.filter(e =>
    e.categoriaEs.toLowerCase().includes(c) ||
    e.categoriaEn.toLowerCase().includes(c)
  );
}
