export interface MedicationEntry {
  id: string;
  name: string;
  genericName: string;
  brandNames: string[];
  class: string;
  category: string;
  icd11Related: string[];
  mechanism: string;
  commonUses: string[];
  sideEffects: string[];
  warnings: string[];
  patientTip: string;
  nameEs: string;
}

// Helper to build entries compactly
