export const MEDICATIONS: Record<string, MedicationEntry> = {};
for (const med of medsArray) {
  MEDICATIONS[med.id] = med;
}

// ========== Utility Functions ==========

export function getMedication(id: string): MedicationEntry | undefined {
  return MEDICATIONS[id];
}

export function searchMedications(query: string): MedicationEntry[] {
  const q = query.toLowerCase();
  return medsArray.filter(med =>
    med.name.toLowerCase().includes(q) ||
    med.genericName.toLowerCase().includes(q) ||
    med.brandNames.some(b => b.toLowerCase().includes(q)) ||
    med.class.toLowerCase().includes(q) ||
    med.category.toLowerCase().includes(q) ||
    med.commonUses.some(u => u.toLowerCase().includes(q))
  );
}

export function getMedicationsByClass(drugClass: string): MedicationEntry[] {
  const cls = drugClass.toLowerCase();
  return medsArray.filter(med => med.class.toLowerCase() === cls);
}

export function getMedicationsByCategory(category: string): MedicationEntry[] {
  const cat = category.toLowerCase();
  return medsArray.filter(med => med.category.toLowerCase() === cat);
}

export function getMedicationsForCondition(icd11Code: string): MedicationEntry[] {
  return medsArray.filter(med => med.icd11Related.includes(icd11Code));
}

export function getAllCategories(): string[] {
  return [...new Set(medsArray.map(med => med.category))];
}

export function getAllClasses(): string[] {
  return [...new Set(medsArray.map(med => med.class))];
}

export function getMedicationCount(): number {
  return medsArray.length;
}
