export function buscarMedicinaDeportiva(termino: string): MedicinaDeportivaEntry[] {
  const t = termino.toLowerCase();
  return MEDICINA_DEPORTIVA_ENTRIES.filter(e =>
    e.nombreEs.toLowerCase().includes(t) ||
    e.nombreEn.toLowerCase().includes(t) ||
    e.descripcionEs.toLowerCase().includes(t) ||
    e.descripcionEn.toLowerCase().includes(t)
  );
}

export function getMedicinaDeportivaById(id: string): MedicinaDeportivaEntry | undefined {
  return MEDICINA_DEPORTIVA_ENTRIES.find(e => e.id === id);
}

export function getMedicinaDeportivaPorCategoria(categoria: string): MedicinaDeportivaEntry[] {
  const c = categoria.toLowerCase();
  return MEDICINA_DEPORTIVA_ENTRIES.filter(e =>
    e.categoriaEs.toLowerCase().includes(c) ||
    e.categoriaEn.toLowerCase().includes(c)
  );
}
