// --- Helper Functions ---

export function buscarMedicinaViaje(termino: string): MedicinaViajeEntry[] {
  const t = termino.toLowerCase();
  return MEDICINA_VIAJE_ENTRIES.filter(e =>
    e.nombreEs.toLowerCase().includes(t) ||
    e.nombreEn.toLowerCase().includes(t) ||
    e.descripcionEs.toLowerCase().includes(t) ||
    e.descripcionEn.toLowerCase().includes(t)
  );
}

export function getMedicinaViajeById(id: string): MedicinaViajeEntry | undefined {
  return MEDICINA_VIAJE_ENTRIES.find(e => e.id === id);
}

export function getMedicinaViajePorCategoria(categoria: string): MedicinaViajeEntry[] {
  const c = categoria.toLowerCase();
  return MEDICINA_VIAJE_ENTRIES.filter(e =>
    e.categoriaEs.toLowerCase().includes(c) ||
    e.categoriaEn.toLowerCase().includes(c)
  );
}
