// ---------------------------------------------------------------------------
// Helper Functions
// ---------------------------------------------------------------------------

/**
 * Busca entradas de neonatologia por termino en nombre o descripcion (ES/EN).
 * Searches neonatology entries by term in name or description (ES/EN).
 */
export function buscarNeonatologia(termino: string): NeonatologiaEntry[] {
  const t = termino.toLowerCase();
  return NEONATOLOGIA_ENTRIES.filter(
    (e) =>
      e.nombreEs.toLowerCase().includes(t) ||
      e.nombreEn.toLowerCase().includes(t) ||
      e.descripcionEs.toLowerCase().includes(t) ||
      e.descripcionEn.toLowerCase().includes(t)
  );
}

/**
 * Obtiene una entrada de neonatologia por su ID.
 * Gets a neonatology entry by its ID.
 */
export function getNeonatologiaById(
  id: string
): NeonatologiaEntry | undefined {
  return NEONATOLOGIA_ENTRIES.find((e) => e.id === id);
}

/**
 * Filtra entradas de neonatologia por categoria (ES/EN).
 * Filters neonatology entries by category (ES/EN).
 */
export function getNeonatologiaPorCategoria(
  categoria: string
): NeonatologiaEntry[] {
  const c = categoria.toLowerCase();
  return NEONATOLOGIA_ENTRIES.filter(
    (e) =>
      e.categoriaEs.toLowerCase().includes(c) ||
      e.categoriaEn.toLowerCase().includes(c)
  );
}

/**
 * Obtiene todas las categorias unicas disponibles.
 * Gets all unique available categories.
 */
export function getCategoriasNeonatologia(): {
  es: string[];
  en: string[];
} {
  const esSet = new Set<string>();
  const enSet = new Set<string>();
  for (const e of NEONATOLOGIA_ENTRIES) {
    esSet.add(e.categoriaEs);
    enSet.add(e.categoriaEn);
  }
  return { es: Array.from(esSet), en: Array.from(enSet) };
}

/**
 * Obtiene los sintomas de una condicion por ID en el idioma especificado.
 * Gets the symptoms of a condition by ID in the specified language.
 */
export function getSintomasPorId(
  id: string,
  idioma: 'es' | 'en' = 'es'
): string[] {
  const entry = getNeonatologiaById(id);
  if (!entry) return [];
  return idioma === 'es' ? entry.sintomasEs : entry.sintomasEn;
}
