export { CONDICIONES_PEDIATRICAS, HITOS_DESARROLLO, NUTRICION_INFANTIL };

// ── Helper functions ────────────────────────────────────────────────

export function buscarPediatria(query: string): PediatriaEntry[] {
  const q = query.toLowerCase();
  return CONDICIONES_PEDIATRICAS.filter(
    (e) =>
      e.nombreEs.toLowerCase().includes(q) ||
      e.nombreEn.toLowerCase().includes(q) ||
      e.categoriaEs.toLowerCase().includes(q) ||
      e.categoriaEn.toLowerCase().includes(q) ||
      e.descripcionEs.toLowerCase().includes(q) ||
      e.descripcionEn.toLowerCase().includes(q)
  );
}

export function getPediatriaById(id: string): PediatriaEntry | undefined {
  return CONDICIONES_PEDIATRICAS.find((e) => e.id === id);
}

export function getHitosPorEdad(meses: number): HitoDesarrollo | undefined {
  return HITOS_DESARROLLO.find((h) => h.edadMeses === meses);
}

export function getNutricionPorEdad(meses: number): NutricionInfantil | undefined {
  return NUTRICION_INFANTIL.find((n) => {
    const rango = n.edadRango;
    const match = rango.match(/^(\d+)-(\d+)/);
    if (match) {
      const lo = parseInt(match[1], 10);
      const hi = parseInt(match[2], 10);
      return meses >= lo && meses <= hi;
    }
    const single = rango.match(/^(\d+)/);
    if (single) {
      return meses === parseInt(single[1], 10);
    }
    return false;
  });
}

export function getPediatriaPorCategoria(cat: string): PediatriaEntry[] {
  const c = cat.toLowerCase();
  return CONDICIONES_PEDIATRICAS.filter(
    (e) =>
      e.categoriaEs.toLowerCase().includes(c) ||
      e.categoriaEn.toLowerCase().includes(c)
  );
}
