// -- Helper functions --------------------------------------------------------

export function buscarPsiquiatria(termino: string): PsiquiatriaEntry[] {
  const t = termino.toLowerCase();
  return PSIQUIATRIA_ENTRIES.filter(
    (e) =>
      e.nombreEs.toLowerCase().includes(t) ||
      e.nombreEn.toLowerCase().includes(t) ||
      e.descripcionEs.toLowerCase().includes(t) ||
      e.descripcionEn.toLowerCase().includes(t)
  );
}

export function getPsiquiatriaById(id: string): PsiquiatriaEntry | undefined {
  return PSIQUIATRIA_ENTRIES.find((e) => e.id === id);
}

export function getPsiquiatriaPorCategoria(
  categoria: string
): PsiquiatriaEntry[] {
  const c = categoria.toLowerCase();
  return PSIQUIATRIA_ENTRIES.filter(
    (e) =>
      e.categoriaEs.toLowerCase().includes(c) ||
      e.categoriaEn.toLowerCase().includes(c)
  );
}
