// -----------------------------------------------------------------------------
// SEARCH & FILTER FUNCTIONS
// -----------------------------------------------------------------------------

/**
 * Busca entradas de rehabilitacion por texto en ambos idiomas.
 * Searches rehabilitation entries by text in both languages.
 *
 * @param query - Texto de busqueda / Search text
 * @returns Entradas que coincidan / Matching entries
 */
export function buscarRehabilitacion(query: string): RehabilitacionEntry[] {
  const q = query.toLowerCase().trim();
  if (!q) return [];

  return rehabilitacionEnciclopedia.filter((entry) => {
    const searchableFields = [
      entry.nombreEs,
      entry.nombreEn,
      entry.categoriaEs,
      entry.categoriaEn,
      entry.descripcionEs,
      entry.descripcionEn,
      ...entry.indicacionesEs,
      ...entry.indicacionesEn,
      ...entry.tecnicasEs,
      ...entry.tecnicasEn,
      ...entry.beneficiosEs,
      ...entry.beneficiosEn,
      ...entry.ejerciciosEs,
      ...entry.ejerciciosEn,
    ];

    return searchableFields.some((field) => field.toLowerCase().includes(q));
  });
}

/**
 * Obtiene una entrada de rehabilitacion por su ID.
 * Gets a rehabilitation entry by its ID.
 *
 * @param id - Identificador unico / Unique identifier
 * @returns La entrada encontrada o undefined / The found entry or undefined
 */
export function getRehabilitacionById(
  id: string
): RehabilitacionEntry | undefined {
  return rehabilitacionEnciclopedia.find((entry) => entry.id === id);
}

/**
 * Filtra entradas de rehabilitacion por categoria (en espanol o ingles).
 * Filters rehabilitation entries by category (in Spanish or English).
 *
 * @param categoria - Nombre de la categoria / Category name
 * @returns Entradas de esa categoria / Entries in that category
 */
export function getRehabilitacionPorCategoria(
  categoria: string
): RehabilitacionEntry[] {
  const cat = categoria.toLowerCase().trim();
  if (!cat) return [];

  return rehabilitacionEnciclopedia.filter(
    (entry) =>
      entry.categoriaEs.toLowerCase() === cat ||
      entry.categoriaEn.toLowerCase() === cat
  );
}
