export const saludHombreEnciclopedia: SaludHombreEntry[] = JSON.parse(rawData);

// ---------------------------------------------------------------------------
// Funciones auxiliares / Helper functions
// ---------------------------------------------------------------------------

export function buscarSaludHombre(query: string): SaludHombreEntry[] {
  const q = query.toLowerCase().trim();
  if (!q) return [];
  return saludHombreEnciclopedia.filter((e) => {
    const fields = [
      e.id, e.nombreEs, e.nombreEn, e.categoriaEs, e.categoriaEn,
      e.descripcionEs, e.descripcionEn,
      ...e.sintomasEs, ...e.sintomasEn,
      ...e.factoresRiesgoEs, ...e.factoresRiesgoEn,
      ...e.datosClaveEs, ...e.datosClaveEn,
    ];
    return fields.some((f) => f.toLowerCase().includes(q));
  });
}

export function getSaludHombreById(id: string): SaludHombreEntry | undefined {
  return saludHombreEnciclopedia.find((e) => e.id === id);
}

export function getSaludHombrePorCategoria(cat: string): SaludHombreEntry[] {
  const c = cat.toLowerCase().trim();
  return saludHombreEnciclopedia.filter(
    (e) => e.categoriaEs.toLowerCase() === c || e.categoriaEn.toLowerCase() === c
  );
}

export function getSaludHombreCount(): number {
  return saludHombreEnciclopedia.length;
}
