/** Retrieve a single entry by ID */
export function getPulmonologyEntry(id: string): PulmonologyEntry | undefined {
  return PULMONOLOGY_ENTRIES[id];
}

/** Search entries by keyword across name, description, clinical features, diagnostics, and treatment */
export function searchPulmonology(query: string): PulmonologyEntry[] {
  const q = query.toLowerCase();
  return Object.values(PULMONOLOGY_ENTRIES).filter(entry =>
    entry.name.toLowerCase().includes(q) ||
    entry.nameEs.toLowerCase().includes(q) ||
    entry.description.toLowerCase().includes(q) ||
    entry.clinicalFeatures.some(f => f.toLowerCase().includes(q)) ||
    entry.treatment.some(t => t.toLowerCase().includes(q)) ||
    entry.diagnostics.some(d => d.toLowerCase().includes(q))
  );
}

/** Get all entries for a given category */
export function getPulmonologyByCategory(category: PulmonologyCategory): PulmonologyEntry[] {
  return Object.values(PULMONOLOGY_ENTRIES).filter(e => e.category === category);
}

/** Total number of entries */
export function getPulmonologyCount(): number {
  return Object.keys(PULMONOLOGY_ENTRIES).length;
}

/** Get entries with emergency signs containing a specific keyword */
export function searchPulmonologyEmergencies(query: string): PulmonologyEntry[] {
  const q = query.toLowerCase();
  return Object.values(PULMONOLOGY_ENTRIES).filter(entry =>
    entry.emergencySigns.some(s => s.toLowerCase().includes(q))
  );
}

/** Get all unique ICD-11 codes with their entry names */
export function getPulmonologyICD11Map(): Record<string, string> {
  const map: Record<string, string> = {};
  for (const entry of Object.values(PULMONOLOGY_ENTRIES)) {
    map[entry.icd11] = entry.name;
  }
  return map;
}
