// ---------------------------------------------------------------------------
// Helper functions
// ---------------------------------------------------------------------------

export function buscarToxicologia(termino: string): ToxicologiaEntry[] {
  const t = termino.toLowerCase();
  return TOXICOLOGIA_ENTRIES.filter(e =>
    e.nombreEs.toLowerCase().includes(t) ||
    e.nombreEn.toLowerCase().includes(t) ||
    e.descripcionEs.toLowerCase().includes(t) ||
    e.descripcionEn.toLowerCase().includes(t)
  );
}

export function getToxicologiaById(id: string): ToxicologiaEntry | undefined {
  return TOXICOLOGIA_ENTRIES.find(e => e.id === id);
}

export function getToxicologiaPorCategoria(categoria: string): ToxicologiaEntry[] {
  const c = categoria.toLowerCase();
  return TOXICOLOGIA_ENTRIES.filter(e =>
    e.categoriaEs.toLowerCase().includes(c) ||
    e.categoriaEn.toLowerCase().includes(c)
  );
}
